package com.educationalpractice.navigator;

import androidx.appcompat.app.AppCompatActivity;

import android.content.Intent;
import android.os.Bundle;
import android.webkit.WebView;

public class MapActivity extends AppCompatActivity {

    final String html_default = "<img src=\"data:image/png;base64,\" alt=\"\" />\n";
    final String html_place = "<img src=\"data:image/png;base64,\" alt=\"\" />";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_map);

        WebView webView = findViewById(R.id.web_view);
        Bundle bundle = getIntent().getExtras();

        String MIME_TYPE = "text/html; charset=utf-8";
        String ENCODING = "UTF-8";

        if (bundle == null) {
            webView.loadData(html_default, MIME_TYPE, ENCODING);
        } else {
            webView.loadData(html_place, MIME_TYPE, ENCODING);
            webView.postDelayed(() -> webView.scrollTo(1400, 0), 300);
        }
    }
}